import React, { useState, useEffect } from 'react';
import './index.css';
import axios from 'axios';
//import Pagination from '../Pagination/index';
import { usePagination } from '@material-ui/lab/Pagination';


const App = () => {
const [datas, setDatas] = useState([]);
const [searchName, setSearchName] = useState("");
console.log("value searchTerm", searchName);

useEffect(() => {
  axios.get(`https://data.ratp.fr/api/records/1.0/search/?dataset=liste-des-commerces-de-proximite-agrees-ratp&q=&rows=993&sort=code_postal&facet=dea_fermeture&facet=ville`, {}, { withCredentials: true })
  .then((response) => {
          console.log('objet recu : ', response.data);
          setDatas(response.data.records);
          });
},[]);
console.log('datas', datas);
  return (
    <div className="App">
    <input
        type="text"
        placeholder="Entrez le nom de votre commerce"
        onChange={(event) => {
          setSearchName(event.target.value);
        }}/>
    {
          datas.filter(data => (data.fields.tco_libelle.toLowerCase().includes(searchName.toLowerCase()))).map((data, i) => 
          <div className="App_datas" key={i}>
          <h3>libell√© : {data.fields.tco_libelle}</h3>
          <h3>ville : {data.fields.ville}</h3>
          <h3>code postal : {data.fields.code_postal}</h3>
          </div>
          )
      }
      <Pagination count={10} showFirstButton showLastButton />
    </div>
  );
}

export default App;
